INSERT INTO `aiico_facileforms_scripts` VALUES (1,1,'FF','ff_anychecked','Any Checked','Validate that any radio button or checkbox of a group is checked.','Element Validation','function ff_anychecked(element, message)\r\n{\r\n    // get plain name\r\n   // swal(document.ff_form52[\'ff_nm_bfQuickMode6303958[]\']);\r\n   // swal(document.getElementById(element.id).id);\r\n    var name = \'\';\r\n    if(!element.name){\r\n    	if(element.length && element.length != 0){\r\n           name = element[0].name;\r\n           var cnt = 0;\r\n           for (var i = 0; i < element.length; i++)\r\n             if (element[i].name==name) \r\n              if (element[i].checked) cnt++;                      \r\n           if (cnt==0) {\r\n            ff_validationFocus(element[0].name);\r\n            if (message==\'\') message = \"Please check or select \"+name+\".\\n\";\r\n            return message;\r\n           } // if \r\n\r\n        } else {\r\n           return \'\';\r\n        }\r\n    } else {\r\n      name = element.name;\r\n      if (name.substr(0,6) == \'ff_nm_\') name = name.substring(6,name.length-2);\r\n\r\n      // loop through elements and count selections\r\n      var cnt = 0;\r\n      for (var i = 0; i < ff_elements.length; i++)\r\n        if (ff_elements[i][2]==name) \r\n            if (ff_getElementByIndex(i).checked) cnt++;\r\n\r\n      // if none selected, emitt error\r\n      if (cnt==0) {\r\n        ff_validationFocus(element.name);\r\n        if (message==\'\') message = \"Please check or select \"+name+\".\\n\";\r\n        return message;\r\n      } // if\r\n    }\r\n    \r\n    return \'\';\r\n} // ff_anychecked'),(2,1,'FF','ff_checked','Checked','Validate that radio button or checkbox is checked.','Element Validation','function ff_checked(element, message)\r\n{\r\n    if (element.checked) \r\n        return \'\'; \r\n    else {\r\n        if (message==\'\') message = element.name+\" is not checked.\\n\";\r\n        ff_validationFocus(element.name);\r\n        return message;\r\n    } \r\n}'),(3,1,'FF','ff_checkedValue','Get checked value','Get value of the currently checked radiobutton.','Untyped','function ff_checkedValue(name)\r\n{\r\n    // loop through elements and find checked\r\n    for (i = 0; i < ff_elements.length; i++)\r\n        if (ff_elements[i][2]==name) {\r\n            e = ff_getElementByIndex(i);\r\n            if (e.checked) return e.value;\r\n        } // if \r\n    return \'\';\r\n} // ff_checkedValue'),(4,1,'FF','ff_countQuerySelections','Count Query Selections','Counts how many checkboxes/radiobuttons are checked/selected in a query list element','Untyped','function ff_countQuerySelections(name)\r\n{\r\n    var id = ff_getIdByName(name);\r\n    var cnt = ff_queryRows[id].length;\r\n    var pagesize = ff_queryPageSize[id];\r\n    if (pagesize>0) {\r\n        var currpage = ff_queryCurrPage[id];\r\n        var p;\r\n        for (p = 1; p < currpage; p++) cnt -= pagesize;\r\n        if (cnt > pagesize) cnt = pagesize;\r\n    } // if\r\n    var curr;\r\n    var sels = 0;\r\n    for (curr = 0; curr < cnt; curr++)\r\n        if (document.getElementById(\'ff_cb\'+id+\'_\'+curr).checked) sels++;\r\n    return sels;\r\n} // ff_countQuerySelections'),(5,1,'FF','ff_dollaramount2dp','Dollar Amount with 2 Decimal Places','Validate that a dollar amount with two decimal places is entered.','Element Validation','function ff_dollaramount2dp(element, message)\r\n{\r\n    var ex  = /^\\d+$|^\\d+\\.\\d{2}$/;\r\n    if (!ex.test(element.value)) {\r\n        if (message==\'\') message = element.name+\" must be a number with two decimal places.\\n\";\r\n        ff_validationFocus(element.name);\r\n        return message;\r\n    } // if\r\n    return \'\';\r\n} // ff_dollaramount2dp'),(6,1,'FF','ff_expString','String export','Export string function: escapes special characters of a string','Untyped','function ff_expString(text)\r\n{\r\n    text = trim(text);\r\n    var i;\r\n    var o = \'\';\r\n    for(i = 0; i < text.length; i++) {\r\n        c = text.charAt(i);\r\n        switch(c) {\r\n            case \';\' : o += \'\\\\x3B\'; break;\r\n            case \',\' : o += \'\\\\x2C\'; break;\r\n            case \'&\' : o += \'\\\\x26\'; break;\r\n            case \'<\' : o += \'\\\\x3C\'; break;\r\n            case \'>\' : o += \'\\\\x3E\'; break;\r\n            case \'\\\'\': o += \'\\\\x27\'; break;\r\n            case \'\\\\\': o += \'\\\\x5C\'; break;\r\n            case \'\"\' : o += \'\\\\x22\'; break;\r\n            case \'\\n\': o += \'\\\\n\'; break;\r\n            case \'\\r\': o += \'\\\\r\'; break;\r\n            default: o += c;\r\n        } // switch\r\n    } // for\r\n    return o;\r\n} // expString'),(7,1,'FF','ff_flashupload_not_empty','Flash Upload Not Empty (QuickMode only!)','Validates if a flash upload is empty or not.','Element Validation','function ff_flashupload_not_empty(element, message)\r\n{\r\n    if(typeof bfSummarizers == \"undefined\") { \r\n        if(typeof bfUseErrorAlerts == \'undefined\'){\r\n            swal(\"Flash upload validation only available in QuickMode!\");\r\n        } else {\r\n            alert(\"Flash upload validation only available in QuickMode!\");\r\n        } \r\n    return \'\';}\r\n    if(JQuery(\'#bfFlashFileQueue\'+element.id.split(\'ff_elem\')[1]).html() != \'\') return \'\';\r\n    if (message==\'\') message = \"Please enter \"+element.name+\".\\n\";\r\n    ff_validationFocus(element.name);\r\n    return message;\r\n} // ff_valuenotempty'),(8,1,'FF','ff_getfocus','Get focus','Get the focus. Usually this is for the first element of the form/page.','Element Init','function ff_getfocus(element,condition)\r\n{\r\n    if(!element.name){\r\n    	if(element.length && element.length != 0){\r\n    		element[0].focus();\r\n    	}\r\n    }else{\r\n    	element.focus();\r\n    }\r\n}'),(9,1,'FF','ff_getQuerySelectedRows','Get Query Selected Rows','Returns the selected rows in a 2-dimensional array','Untyped','function ff_getQuerySelectedRows(name)\r\n{\r\n    var id = ff_getIdByName(name);\r\n    var rcnt = ff_queryRows[id].length;\r\n    var cnt = rcnt;\r\n    var pagesize = ff_queryPageSize[id];\r\n    if (pagesize>0) {\r\n        var currpage = ff_queryCurrPage[id];\r\n        var p;\r\n        for (p = 1; p < currpage; p++) cnt -= pagesize;\r\n        if (cnt > pagesize) cnt = pagesize;\r\n    } // if\r\n    var curr, r;\r\n    var selcnt = 0;\r\n    var sels = new Array;\r\n    for (curr = 0; curr < cnt; curr++) {\r\n        var elem = document.getElementById(\'ff_cb\'+id+\'_\'+curr);\r\n        if (elem.checked) {\r\n            var ident = elem.value;\r\n            for (r = 0; r < rcnt; r++)\r\n                if (ff_queryRows[id][r][0] == ident) {\r\n                    sels[selcnt++] = ff_queryRows[id][r];\r\n                    break;\r\n                } // if\r\n        } // if\r\n    } // for\r\n    return sels;\r\n} // ff_getQuerySelectedRows'),(10,1,'FF','ff_getQuerySelections','Get Query Selected ID\'s','Returns the column values of the checked/selected checkboxes/radiobuttons in an array','Untyped','function ff_getQuerySelections(name)\r\n{\r\n    var id = ff_getIdByName(name);\r\n    var cnt = ff_queryRows[id].length;\r\n    var pagesize = ff_queryPageSize[id];\r\n    if (pagesize>0) {\r\n        var currpage = ff_queryCurrPage[id];\r\n        var p;\r\n        for (p = 1; p < currpage; p++) cnt -= pagesize;\r\n        if (cnt > pagesize) cnt = pagesize;\r\n    } // if\r\n    var curr;\r\n    var selcnt = 0;\r\n    var sels = new Array;\r\n    for (curr = 0; curr < cnt; curr++) {\r\n        var elem = document.getElementById(\'ff_cb\'+id+\'_\'+curr);\r\n        if (elem.checked) \r\n            sels[selcnt++] = elem.value;\r\n    } // for\r\n    return sels;\r\n} // ff_getQuerySelections'),(11,1,'FF','ff_htmlvaluenotempty','HTML Texteditor Not Empty','Checks if an HTML editor element is empty or not','Element Validation','function ff_htmlvaluenotempty( element, message ){\r\n    \r\n    if( typeof bf_htmltextareanames == \"undefined\" ) return \'\';\r\n\r\n    var content = \"\";\r\n\r\n    for(var i = 0; i < bf_htmltextareanames.length; i++){\r\n      if( element.name == bf_htmltextareanames[i] ) {\r\n          eval(\"content = \" + bf_htmltextareas[i]);\r\n          break;\r\n      }\r\n    }\r\n    if (JQuery.trim(content) == \"\") {\r\n        if (message==\'\') message = element.name+\" must not be empty.\\n\";\r\n        return message;\r\n    } // if\r\n    return \'\';\r\n}'),(12,1,'FF','ff_impString','String import','Import string function: unescapes c-coded characters of a string','Untyped','function ff_impString(text)\r\n{\r\n    var str = \'\';\r\n    var ss = 0;\r\n    var s;\r\n    var tl = text.length;\r\n    var hexdigs = \"0123456789abcdefABCDEF\";\r\n    while (ss < tl) {\r\n        s = text.charAt(ss++);\r\n        if (s == \'\\\\\') {\r\n            if (ss < tl) s = text.charAt(ss++); else s = 0;\r\n            switch (s) {\r\n                case 0   : break;\r\n                case \'e\' : str += \'\\33\'; break;\r\n                case \'t\' : str += \'\\t\'; break;\r\n                case \'r\' : str += \'\\r\'; break;\r\n                case \'n\' : str += \'\\n\'; break;\r\n                case \'f\' : str += \'\\f\'; break;\r\n                case \'x\' : {\r\n                    if (ss < tl) s = text.charAt(ss++); else s = 0;\r\n                    var ch = \'\';\r\n                    while (hexdigs.indexOf(s)>=0 && ch.length < 2) {\r\n                        ch += s;\r\n                        if (ss < tl) s = text.charAt(ss++); else s = 0;\r\n                    } // while\r\n                    while (ch.length < 2) ch = \'0\'+ch;\r\n                    str += unescape(\'%\'+ch);\r\n                    if (s) ss--;\r\n                    break;\r\n                }\r\n                default:\r\n                    str += s;\r\n            } // switch\r\n        } else\r\n            str += s;\r\n    } // while\r\n    return str;\r\n} // impString'),(13,1,'FF','ff_integer','Integer Number','Validate that an integer value is entered.','Element Validation','function ff_integer(element, message)\r\n{\r\n    var ex  = /(^-?\\d\\d*$)/;\r\n    if (!ex.test(element.value)) {\r\n        if (message==\'\') message = element.name+\" must be integer.\\n\";\r\n        ff_validationFocus(element.name);\r\n        return message;\r\n    } // if\r\n    return \'\';\r\n} // ff_integer'),(14,1,'FF','ff_integeramount','Positive Integer','Validate that an positive integer value is entered.','Element Validation','function ff_integeramount(element, message)\r\n{\r\n    var ex  = /(^-?\\d\\d*$)/;\r\n    if (!ex.test(element.value)) {\r\n        if (message==\'\') message = element.name+\" must be integer.\\n\";\r\n        ff_validationFocus(element.name);\r\n        return message;\r\n    } // if\r\n    return \'\';\r\n} // ff_integeramount'),(15,1,'FF','ff_integer_or_empty','Integer or empty','Validate that either an integer value or nothing is entered.','Element Validation','function ff_integer_or_empty(element, message)\r\n{\r\n    if (element.value != \'\') {\r\n        var ex  = /(^-?\\d\\d*$)/;\r\n        if (!ex.test(element.value)) {\r\n            if (message==\'\') message = element.name+\" must be integer.\\n\";\r\n            ff_validationFocus(element.name);\r\n            return message;\r\n        } // if\r\n    } // if\r\n    return \'\';\r\n} // ff_integer_or_empty'),(16,1,'FF','ff_nextpage','Next page','Switchs to the next page.','Element Action','function ff_nextpage(element, action)\r\n{\r\n    if (ff_currentpage < ff_lastpage) {\r\n        ff_switchpage(ff_currentpage+1);\r\n        self.scrollTo(0,0);\r\n    }\r\n} // ff_nextpage'),(17,1,'FF','ff_page1','Page 1','Switches to the first page.','Element Action','function ff_page1(element, action)\r\n{\r\n    ff_switchpage(1);\r\n} // ff_page1'),(18,1,'FF','ff_page2','Page 2','Switches to the second page.','Element Action','function ff_page2(element, action)\r\n{\r\n    if (ff_lastpage >= 2) ff_switchpage(2);\r\n} // ff_page2'),(19,1,'FF','ff_page3','Page 3','Switches to the third page.','Element Action','function ff_page3(element, action)\r\n{\r\n    if (ff_lastpage >= 3) ff_switchpage(3);\r\n} // ff_page3'),(20,1,'FF','ff_previouspage','Previous page','Switches to the previous page.','Element Action','function ff_previouspage(element, action)\r\n{\r\n    if (ff_currentpage > 1){\r\n        ff_switchpage(ff_currentpage-1);\r\n        self.scrollTo(0,0);\r\n    }\r\n} // ff_previouspage'),(21,1,'FF','ff_real','Real Number','Validate that a real number is entered.','Element Validation','function ff_real(element, message)\r\n{\r\n    var ex  = /(^-?\\d\\d*\\.?\\d*$)/;\r\n    if (!ex.test(element.value)) {\r\n        if (message==\'\') message = element.name+\" must be a number.\\n\";\r\n        ff_validationFocus(element.name);\r\n        return message;\r\n    } // if\r\n    return \'\';\r\n} // ff_real'),(22,1,'FF','ff_realamount','Positive Real Number','Validate that a positive real number is entered.','Element Validation','function ff_realamount(element, message)\r\n{\r\n    var ex  = /(^\\d\\d*\\.?\\d*$)/;\r\n    if (!ex.test(element.value)) {\r\n        if (message==\'\') message = element.name+\" must be a number.\\n\";\r\n        ff_validationFocus(element.name);\r\n        return message;\r\n    } // if\r\n    return \'\';\r\n} // ff_realamount'),(23,1,'FF','ff_resetForm','Reset form','Reset all form inputs to the initial values.','Element Action','function ff_resetForm(element, action)\r\n{\r\n    eval(\'document.\'+ff_processor.form_id).reset();\r\n} // ff_resetForm'),(24,1,'FF','ff_securitycode_entered','Security code entered','Check that a security code was entered.','Element Validation','function ff_securitycode_entered(element, message)\r\n{\r\n    var ex  = /(^\\d{5}$)/;\r\n    if (!ex.test(element.value)) {\r\n        if (message==\'\') message = \"Security code must be entered as five digits.\\n\";\r\n        ff_validationFocus(element.name);\r\n        return message;\r\n    } // if\r\n    return \'\';\r\n} // ff_securitycode_entered'),(25,1,'FF','ff_securitycode_ok','Security code ok','Check that a valid security code was entered.','Element Validation','function ff_securitycode_ok(element, message)\r\n{\r\n<?php\r\n    global $ff_seccode;\r\n    if (!isset($ff_seccode)) { \r\n        mt_srand((double)microtime()*1000000);\r\n        $ff_seccode = mt_rand(10000, 99999);\r\n        JFactory::getSession()->set(\'ff_seccode\', $ff_seccode);\r\n    } // if\r\n    $enc = array();\r\n    mt_srand((double)microtime()*1000000);\r\n    for ($i = 0; $i < 256; $i++) $enc[] = mt_rand(0, 255);\r\n    $seccode = (string)$ff_seccode;\r\n    $hash = \'\';\r\n    $ini = $ff_seccode % 240;\r\n    for ($i = 0; $i < 5; $i++) {\r\n        $d = $ini+intval($seccode{$i});\r\n        for ($j = 0; $j < 7; $j++) $d = $enc[$d];\r\n        $hash .= $d;\r\n        $ini = ($ini+$d) % 240;\r\n    } // for\r\n    return\r\n        \"var enc = \".$this->expJsValue($enc).\";\\n\".\r\n        \"var seccode = new String(element.value);\\n\".\r\n        \"if (seccode.length==5) {\\n\".\r\n        \"    var hash = \'\';\\n\".\r\n        \"    var ini = parseInt(element.value) % 240;\\n\".\r\n        \"    var i, j;\\n\".\r\n        \"    for (i = 0; i < 5; i++) {\\n\".\r\n        \"        var d = ini+parseInt(seccode.charAt(i));\\n\".\r\n        \"        for (j = 0; j < 7; j++) d = enc[d];\\n\".\r\n        \"        hash += d;\\n\".\r\n        \"        ini = (ini+d) % 240;\\n\".\r\n        \"    } // for\\n\".\r\n        \"    if (hash == \'$hash\') return \'\';\\n\". \r\n        \"} // if\\n\".\r\n        \"if (message==\'\') message = \\\"Security code is missing or wrong.\\\\n\\\";\\n\".\r\n        \"ff_validationFocus(element.name);\\n\".\r\n        \"return message;\\n\";\r\n?>\r\n} // ff_securitycode_ok'),(26,1,'FF','ff_selectedValues','Get selected values as list','Get values of selected options of a select list as list:\r\n\r\nx = ff_selectedValues(\'myselectlist\');\r\n// x = green,red','Untyped','function ff_selectedValues(name)\r\n{\r\n    vals = \'\';\r\n    opts = ff_getElementByName(name).options;\r\n    for (o = 0; o < opts.length; o++)\r\n        if (opts[o].selected) {\r\n            if (vals != \'\') vals += \',\';\r\n            vals += opts[o].value;\r\n        } // if\r\n    return vals;\r\n} // ff_selectedValues'),(27,1,'FF','ff_setChecked','Set radiobutton checked','Set a radiobutton checked/unchecked','Untyped','function ff_setChecked(name, value, checked)\r\n{\r\n    if (arguments.length<3) checked = true;\r\n    for (var i = 0; i < ff_elements.length; i++)\r\n        if (ff_elements[i][2]==name) {\r\n            var e = ff_getElementByIndex(i);\r\n            if (e.value == value) {\r\n                e.checked = checked;\r\n                break;\r\n            } // if\r\n        } // if\r\n} // ff_setChecked'),(28,1,'FF','ff_setSelected','Select options of  select list','Select options in a Select List element.\r\n\r\nff_setSelected(\'mylist\', \'green\'); // select green in single or multi mode, unselect all other options\r\nff_setSelected(\'mylist\', \'red,green\'); // select red and green in multi mode, unselect all other options','Untyped','function ff_setSelected(name, list)\r\n{\r\n    ids = list.split(\',\')\r\n    opts = ff_getElementByName(name).options;\r\n    for (o = 0; o < opts.length; o++) {\r\n        state = false;\r\n        for (i = 0; i < ids.length; i++) \r\n            if (ids[i]==opts[o].value) {\r\n                state = true;\r\n                break;\r\n            } // if\r\n        opts[o].selected = state;\r\n    } // for\r\n} // ff_setSelected'),(29,1,'FF','ff_showaction','Show action','Displays the element action.','Element Action','function ff_showaction(element, action)\r\n{\r\n    if(typeof bfUseErrorAlerts == \'undefined\'){\r\n        swal(\'Action \'+action+\' performed by element \'+element.id);\r\n    } else {\r\n        alert(\'Action \'+action+\' performed by element \'+element.id);\r\n    }\r\n   \r\n}'),(30,1,'FF','ff_showelementinit','Show element initialization','Display the element initialization (mainly for debugging)','Element Init','function ff_showelementinit(element,condition)\r\n{\r\n    if(!element.name) {\r\n    	if(element.length && element.length != 0){\r\n            if(typeof bfUseErrorAlerts == \'undefined\'){\r\n                swal(\'Initialization of \'+element[0].name+\' at \'+condition);\r\n            } else {\r\n                alert(\'Initialization of \'+element[0].name+\' at \'+condition);\r\n            }\r\n        }\r\n    } else {\r\n        if(typeof bfUseErrorAlerts == \'undefined\'){\r\n            swal(\'Initialization of \'+element.name+\' at \'+condition);\r\n        } else {\r\n            alert(\'Initialization of \'+element.name+\' at \'+condition);\r\n        }\r\n    }\r\n}'),(31,1,'FF','ff_showforminit','Show form initialization','Show when form initialization is run (for debugging)','Form Init','function ff_showforminit()\r\n{\r\n      if(typeof bfUseErrorAlerts == \'undefined\'){\r\n        swal(\'Form initialization\');\r\n    } else {\r\n        alert(\'Form initialization\');\r\n    }\r\n}'),(32,1,'FF','ff_showsubmitted','Show submitted status','Display submit status as user feedback.','Form Submitted','function ff_showsubmitted(status, message)\r\n{\r\n    if(typeof bfUseErrorAlerts == \'undefined\'){\r\n        swal(message);\r\n    } else {\r\n        alert(message);\r\n    }\r\n} // ff_showsubmitted'),(33,1,'FF','ff_showvalidation','Show element validation','Display element validation (for debugging)','Element Validation','function ff_showvalidation(element, message)\r\n{\r\n    if(typeof bfUseErrorAlerts == \'undefined\'){\r\n        swal(\'Validation of \'+element.name+\" with message: \\n\"+message);\r\n    } else {\r\n        alert(\'Validation of \'+element.name+\" with message: \\n\"+message);\r\n    }\r\n    return \'\';\r\n}'),(34,1,'FF','ff_submittedhome','Return to homepage','Display submit status and then return to the home page of the site.','Form Submitted','function ff_submittedhome(status, message)\r\n{\r\n    if(typeof bfUseErrorAlerts == \'undefined\'){\r\n        swal(message+\"\\nYou will be redirected to the home page now.\");\r\n    } else {\r\n        alert(message+\"\\nYou will be redirected to the home page now.\");\r\n    }\r\n    ff_returnHome();\r\n} // ff_submittedhome'),(35,1,'FF','ff_unchecked','Unchecked','Validate that radio button or checkbox is unchecked.','Element Validation','function ff_unchecked(element, message)\r\n{\r\n    if (!element.checked) \r\n        return \'\'; \r\n    else {\r\n        if (message==\'\') message = element.name+\" is checked.\\n\";\r\n        ff_validationFocus(element.name);\r\n        return message;\r\n    } // if\r\n} // ff_unchecked'),(36,1,'FF','ff_validate_date_DDMMYYYY','Validate Date DD/MM/YYYY format','Validates that the date in a textfield is correctly formatted in DD/MM/YYYY format.\r\nCan be used with a textfield element and/or the Calendar element.','Element Validation','function ff_validate_date_DDMMYYYY(element, message)\r\n{\r\n   // Regular expression used to check if date is in correct format\r\n   var pattern = /[0-3][0-9]\\/(0|1)[0-9]\\/(19|20)[0-9]{2}/;\r\n   if(pattern.test(element.value))\r\n   {\r\n      var date_array = element.value.split(\'/\');\r\n      var day = date_array[0];\r\n\r\n      // Attention! Javascript consider months in the range 0 - 11\r\n      var month = date_array[1] - 1;\r\n      var year = date_array[2];\r\n\r\n      // This instruction will create a date object\r\n      source_date = new Date(year,month,day);\r\n\r\n      if(year != source_date.getFullYear())\r\n      {\r\n         return message == \'\' ? \'Element \' + element.name + \' failed my test\' : message;\r\n      }\r\n\r\n      if(month != 